public static long ip2long(String ip) {
    String[] ipArray = ip.split("\\.");
    List<Long> ipNums = new ArrayList<Long>();
    for (String n : ipArray) {
        ipNums.add(Long.valueOf(Long.parseLong(n.trim())));
    }
    long ZhongIPNumTotal = ((Long) ipNums.get(0)).longValue() * 256L * 256L * 256L + ((Long) ipNums.get(1)).longValue() * 256L * 256L + ((Long) ipNums.get(2)).longValue() * 256L + ((Long) ipNums.get(3)).longValue();
    return ZhongIPNumTotal;
}
public static String long2ip(long ipaddr) {
    long y = ipaddr % 256;
    long m = (ipaddr - y) / (256 * 256 * 256);
    long n = (ipaddr - 256 * 256 *256 * m - y) / (256 * 256);
    long x = (ipaddr - 256 * 256 *256 * m - 256 * 256 *n - y) / 256;
    return m + "." + n + "." + x + "." + y;
}

/**
 * 返回40位16进制密文
 * @param str
 * @return
 */
public static String sha(String str) {
    StringBuilder s = new StringBuilder(40);
    try {
        MessageDigest md = MessageDigest.getInstance("sha");
        md.update(str.getBytes());
        for (byte b : md.digest()) {
            String s2 = String.format("%x", b);
            s.append(s2.length() == 2 ? s2 : "0" + s2);
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
    return s.toString();
}

/**
 * 返回32位16进制密文
 * @param str
 * @return
 */
public static String md5(String str) {
    StringBuilder s = new StringBuilder(32);
    try {
        MessageDigest md = MessageDigest.getInstance("md5");
        md.update(str.getBytes());
        for (byte b : md.digest()) {
            String s2 = String.format("%x", b);
            s.append(s2.length() == 2 ? s2 : "0" + s2);
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
    return s.toString();
}

/**
 * 检查密文是否正确
 *
 * @param account  账号
 * @param password 密码
 * @param passwd   32密文
 * @return
 */
public static boolean checkmd(String account, String password, String passwd) {
    boolean flag = false;
    if (passwd.equals(md(account, password))) flag = true;
    return flag;
}

/**
 * 返回32位26进制密文
 *
 * @param account
 * @param password
 * @return
 */
public static String md(String account, String password) {
    StringBuilder s = new StringBuilder(32);
    String str = account + password;
    String pa = md5(str);
    String pb = sha(str);
    s.append(pa.substring(0, 5));
    s.append(pb.substring(5, 10));
    s.append(pa.substring(10, 15));
    s.append(pb.substring(15, 20));
    s.append(pa.substring(26));
    s.append(pb.substring(34));
    return s.toString();
}

