/**
 * <p>功能描述: 对分页的基本数据进行一个简单的封装 </p>
 *
 * @author webrx
 * @version 1.0
 * @date 2020-01-17 15:39
 */
public class Page<T> {
    private int pageNo = 1;//页码，默认是第一页  
    private int pageSize = 15;//每页显示的记录数，默认是15  
    private int totalRecord;//总记录数  
    private int totalPage;//总页数  
    private List<T> results;//对应的当前页记录  
    private Map<String, Object> params = new HashMap<String, Object>();//其他的参数我们把它分装成一个Map对象  

    public int getPageNo() {
        return pageNo;
    }

    public void setPageNo(int pageNo) {
        this.pageNo = pageNo;
    }

    public int getPageSize() {
        return pageSize;
    }

    public void setPageSize(int pageSize) {
        this.pageSize = pageSize;
    }

    public int getTotalRecord() {
        return totalRecord;
    }

    public void setTotalRecord(int totalRecord) {
        this.totalRecord = totalRecord;
        //在设置总页数的时候计算出对应的总页数，在下面的三目运算中加法拥有更高的优先级，所以最后可以不加括号。  
        int totalPage = totalRecord % pageSize == 0 ? totalRecord / pageSize : totalRecord / pageSize + 1;
        this.setTotalPage(totalPage);
    }

    public int getTotalPage() {
        return totalPage;
    }

    public void setTotalPage(int totalPage) {
        this.totalPage = totalPage;
    }

    public List<T> getResults() {
        return results;
    }

    public void setResults(List<T> results) {
        this.results = results;
    }

    public Map<String, Object> getParams() {
        return params;
    }

    public void setParams(Map<String, Object> params) {
        this.params = params;
    }

    @Override
    public String toString() {
        StringBuilder s = new StringBuilder();
        s.append("Page [pageNo=").append(pageNo).append(", pageSize=")
                .append(pageSize).append(", results=").append(results).append(
                ", totalPage=").append(totalPage).append(
                ", totalRecord=").append(totalRecord).append("]");
        return s.toString();
    }
}

/**
 * 设置cookie
 * @param name
 * @param value
 * @param time
 */
function setCookie(name,value,time){
    let now = new Date();
    now.setTime(now.getTime() + (time * 60 * 1000));
    document.cookie = `${name}=${value}; expires=${now.toUTCString()}`;
}

/**
 * 判断cookie存在不存在，如果不存在返回false
 * @param name
 * @returns {boolean}
 */
function existsCookie(name){
    let flag = false;
    let cs = document.cookie.split("; ");
    for(c of cs){
        if(c.startsWith(name+"=")){
            flag = true;
            break;
        }
    }
    return flag;
}

/**
 * 删除指定cookie
 * @param name
 */
function deleteCookie(name){
    if(existsCookie(name)){
        setCookie(name,0,-1);
    }
}


/**
 * 读取如果没有参数，或参数不存在，则返回所有cookie,如果参数存在，则相关的值。
 * @param name
 * @returns {string}
 */
function getCookie(name){
    let cs = document.cookie.split("; ");
    let vv = cs.join(",");
    for(c of cs){
        if(c.startsWith(name+"=")){
            vv = c.substr(c.lastIndexOf("=")+1);
            break;
        }
    }
    return vv;
}

/**
 * <p>功能描述: </p>
 *
 * @author webrx
 * @version 1.0
 * @date 2020-11-24 14:18
 */
public class DbUtil {
    static DruidDataSource dataSource;
    static QueryRunner run;
    static {
        Properties p = new Properties();
        try {
            //src/main/resources/db.properties那个文件
            p.load(Thread.currentThread().getContextClassLoader().getResourceAsStream("db.properties"));
            dataSource = (DruidDataSource) DruidDataSourceFactory.createDataSource(p);
            run = new QueryRunner(dataSource);
        } catch (IOException e) {
            e.printStackTrace();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public static Connection getConnection() {
        try {
            return dataSource.getConnection();
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return null;
    }

    public int count(String sql,Object...params){
        int result = 0;
        try {
            long res = run.query(sql,new ScalarHandler<>(),params);
            result = (int)res;
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return result;
    }

    public Map<String,Object> find(String sql,Object...params){
        Map<String,Object> map = null;
        try {
            map = run.query(sql,new MapHandler(),params);
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return map;
    }

    public List<Map<String,Object>> query(String sql,Object...params){
        List<Map<String,Object>> list = null;
        try {
            list = run.query(sql,new MapListHandler(),params);
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return list;
    }

    public int update(String sql,Object...params){
        int result = 0;
        try {
            result = run.update(sql,params);
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return result;
    }

    public static void colse() {
        dataSource.close();
    }
}

